$.fn.nextOrFirst = function(selector) {
  var next = this.next(selector);
  return (next.length) ? next : this.prevAll(selector).last();
}

$.fn.addSpinner = function() {
  var opts = {
    width: 4,
    lines: 13,
    color: '#888', // #rgb or #rrggbb
    trail: 60,
    position: 'absolute'
  };

  var spinner = new Spinner(opts).spin();
  $(this).append($('<div class="spinner-container"/>').append(spinner.el));
}

$.fn.removeSpinner = function() {
  $(this).find('.spinner-container').remove();
}

$.fn.setupSubquestions = function() {
  var subquestions = $(this).find('.subquestion');
  var self = $(this);

  if (subquestions.length) {
    subquestions.first().show().siblings().hide();
    
    self.find('.subquestion-pagination').remove();
    self.find('.grid-progress').remove();

    var prev = $('<a/>').addClass('prev disabled').text('‹');
    var next = $('<a/>').addClass('next disabled').text('›');
    var pagination = $('<div/>').addClass('subquestion-pagination').append(prev).append(next);
    var actions = self.closest('.prescreener').find('.actions');
    
    actions.prepend(pagination);

    function setHeight() {
      var h = subquestions.parent().removeAttr('style').height();
      subquestions.parent().height(h);
    };

    function slideCard(currentCard, nextCard, direction) {
      currentCard.hide('slide', {direction: direction.hide}, 150, function(){
        nextCard.show('slide', {direction: direction.show}, 150, setHeight);
      });
    };

    function getNextCard(order) {
      var direction = {
        hide: (order == 'next') ? 'left' : 'right',
        show: (order == 'next') ? 'right': 'left'
      };

      var currentSubquestion = $('.subquestion:visible');
      var nextSubquestion = currentSubquestion[order]();

      if (nextSubquestion.length) {
        slideCard(currentSubquestion, nextSubquestion, direction);

        console.log(nextSubquestion.index() + 1);
        $(".grid-progress label .count").text(nextSubquestion.index() + 1);

        if (nextSubquestion.not(":last-child") && nextSubquestion.find('input').is(':checked')) {
          $('.subquestion-pagination .next').removeClass('disabled');        
        } else {
          $('.subquestion-pagination .next').addClass('disabled');
        }

        if (nextSubquestion.is(":first-child")) {
          $('.subquestion-pagination .prev').addClass('disabled');
        } else {
          $('.subquestion-pagination .prev').removeClass('disabled');
        }
      }
    };

    function updateProgressBar() {
      var answered = 0;
      subquestions.each(function(){ answered += $(this).find('input').is(':checked') ? 1 : 0; });
      var w = (answered / subquestions.length) * 100;
      
      bar.animate({'width': w+'%'}, 150);   
      
      if (w == 100) {
        progressBar.addClass('complete');
      } else {
        progressBar.removeClass('complete');
      }
    }

    prev.on('click', function(e) {
      e.preventDefault();
      if (!$(this).hasClass('disabled')) {
        getNextCard('prev');
      }
    });

    next.on('click', function(e) {
      e.preventDefault();
      if (!$(this).hasClass('disabled')) {
        getNextCard('next');
      }
    });

    $("input[type=radio], input[type=checkbox]").on('change', function() {
      if ($(this).is(":checked")) {
        self.find('.next').removeClass('disabled');
      } 

      if ($(this).prop('type') == 'radio') {
        self.find('.next').trigger('click');
      } 

      updateProgressBar();
    });

    setHeight();

    // setup progress bar
    var num = subquestions.length;
    var text = $("<label/>").html('Pertanyaan <span class="count">1</span> Dari ' + num),
        bar = $("<span/>").addClass('bar'),
        progress = $("<div/>").addClass('progress').append(bar),
        progressBar = $("<div/>").addClass('grid-progress').append(text).append(progress);

    actions.append(progressBar);

    updateProgressBar();

    // end setup progress bar
  }

  return this;
}

$.fn.submitAnswer = function() {
  $(this).closest(".prescreener").addSpinner();

  setTimeout(function() {
    getNextQuestion();
  }, 1000);
}

$.fn.resetInputs = function() {
  $(this).find(':input').each(function() {
    switch(this.type) {
      case 'password':
      case 'select-multiple':
      case 'select-one':
      case 'text':
      case 'textarea':
        $(this).val('');
        break;
      case 'checkbox':
      case 'radio':
        this.checked = false;
    }
  });
  return this;
}

function getNextQuestion() {
  var currentQuestion = $('.prescreener.show'),
      nextQuestion = currentQuestion.nextOrFirst('.prescreener');

  currentQuestion.removeClass('show').removeSpinner();

  nextQuestion.resetInputs().addClass('show').find('.js-resize-choices').resizeChoices();
  nextQuestion.setupSubquestions().center();
}

$.fn.resizeChoices = function() {
  if ($(this).is(":visible")){
    var widestChoice = 0,
        $choices = $(this).children();

    $(this).addSpinner();

    $choices.each(function(){
      $(this).removeAttr('style');
      if ($(this).width() > widestChoice) {
        widestChoice = $(this).width();
      }
    });

    $choices.width(widestChoice + 1);
    $(this).removeSpinner();
  }
  return this;
}



$(document).ready(function() {
  $('.js-resize-choices').resizeChoices();
  var prescreener = $(".js-center").setupSubquestions().center();

  $(".num").on('keypress', function(e) {
    var key = e.keyCode || e.which;

    if (48 <= key && key <= 57) {
      // good job
    } else {
      var entry = $.trim(this.value);

      if (key == 13 && entry.length) {
        // submit answer
        $(this).attr("readonly", "readonly").blur().closest('.prescreener').find('.submit').trigger('click');
        $(this).removeAttr('readonly');
      } else {
        e.preventDefault();
        var $this = $(this);
        $this.siblings('.error').addClass('show');
        $this.shake({duration: 200});
        setTimeout(function(){ $this.siblings('.error').removeClass('show'); }, 3000);
      }
    }
  });

  $("input[type=text]").on('keyup', function(){
    var entry = $.trim(this.value);

    if (entry.length) {
      $('.show .actions a').removeClass('disabled');
    } else {
      $(this).val('');
      $('.show .actions a').addClass('disabled');
    }
  });

  $("input[type=radio], input[type=checkbox]").on('change', function() {
    var complete = true;
    var currentQuestionSet = $(this).closest('.prescreener');

    currentQuestionSet.find('.options').each(function() {
      if (!$(this).find('input').is(':checked')) {
        complete = false;
      }
    });

    if (!complete) {
      currentQuestionSet.find('.submit').addClass('disabled');
    } else {
      currentQuestionSet.find('.submit').removeClass('disabled');
    }
  });

  $('a').on('click', function(e){
    e.preventDefault();

    if ($(this).hasClass('disabled')) { 
      e.stopPropagation();
    } else if ($(this).hasClass('submit')) {
      $(this).submitAnswer();
    }
  });

  $(window).smartresize(function(){
    $('.js-resize-choices').resizeChoices();
  });
});                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        